						suchThatAssume = t; 
					}
					Expression(out suchThat, false, true);
				} else if (la.kind == 120) {
					Get();
					x = t; 
					if (IsAssumeTypeKeyword(la)) {
						if (la.kind == 97) {
							Get();
						} else if (la.kind == 36) {
							Get();
						} else if (la.kind == 35) {
							Get();
						} else SynErr(216);
						keywordToken = t; 
					}
					Expression(out exceptionExpr, false, false);
					while (la.kind == 25) {
						Get();
						Rhs(out r);
						rhss.Add(r); 
					}
				} else SynErr(217);
				Expect(33);
				endTok = t; 
			} else if (la.kind == 24) {
				Get();
				SemErr(t, "invalid statement (did you forget the 'label' keyword?)"); 
			} else SynErr(218);
		} else if (la.kind == 120) {
			Get();
			x = t; 
			if (IsAssumeTypeKeyword(la)) {
				if (la.kind == 97) {
