				QuantifierDomain(out bvars, out qattrs, out range, true, true, true);
			}
			Expect(85);
		} else if (StartOf(34)) {
			if (IsIdentifier(la.kind)) {
				QuantifierDomain(out bvars, out qattrs, out range, true, true, true);
			}
		} else SynErr(239);
