		List<Expression> display = null;
		r = dummyRhs;  // to please compiler
		Attributes attrs = null;
		
		if (la.kind == 75) {
			Get();
			newToken = t; 
			if (la.kind == 82) {
				NewArray(out ee, out arrayElementInit, out display);
			} else if (StartOf(5)) {
				TypeAndToken(out x, out ty, false);
				if (la.kind == 82 || la.kind == 84) {
					if (la.kind == 82) {
						NewArray(out ee, out arrayElementInit, out display);
					} else {
						x = null; args = new List<ActualBinding>(); 
						Get();
						if (StartOf(4)) {
							ActualBindings(args);
						}
						Expect(85);
					}
				}
			} else SynErr(244);
			if (ee != null) {
			 if (display != null) {
			   r = new TypeRhs(newToken, ty, ee[0], display);
			 } else {
			   r = new TypeRhs(newToken, ty, ee, arrayElementInit);
			 }
			} else if (args != null) {
			 r = new TypeRhs(newToken, ty, args);
			} else {
			 r = new TypeRhs(newToken, ty);
			}
			
		} else if (la.kind == 92) {
			Get();
			r = new HavocRhs(t); 
		} else if (StartOf(4)) {
			Expression(out e, false, true);
			r = new ExprRhs(e); 
		} else SynErr(245);
		while (la.kind == 79) {
			Attribute(ref attrs);
		}
		r.Attributes = attrs; 
	}

